import React from 'react';

// Vordefinierte Dienstleistungen
const PREDEFINED_SERVICES = [
  {
    kategorie: "VMware & Virtualisierung",
    services: [
      { id: 1, beschreibung: "Konfiguration VMWare-Dienste (HA etc.)", std_pro_einheit: 1 },
      { id: 2, beschreibung: "Installation/Grundkonfiguration Active Directory inkl. Serverrollen (DNS, DHCP etc.)", std_pro_einheit: 2 }
    ]
  },
  {
    kategorie: "Hardware-Arbeiten vor Ort",
    services: [
      { id: 3, beschreibung: "Einbau Hardware vor Ort", std_pro_einheit: 1 },
      { id: 4, beschreibung: "Rackumbau", std_pro_einheit: 3 },
      { id: 5, beschreibung: "Konfiguration Switch Management, pro VLAN", std_pro_einheit: 0.25 }
    ]
  },
  {
    kategorie: "Software-Arbeiten (Remote)",
    services: [
      { id: 6, beschreibung: "Migrationsvorbereitungen (Patchen Altsysteme) pro System", std_pro_einheit: 2 },
      { id: 7, beschreibung: "Einrichtung/Migration Active Directory pro User", std_pro_einheit: 0.1 },
      { id: 8, beschreibung: "Konfiguration pro Gruppenrichtlinie", std_pro_einheit: 0.25 }
    ]
  },
  {
    kategorie: "Exchange Server",
    services: [
      { id: 9, beschreibung: "Installation/Grundkonfiguration Exchange Server", std_pro_einheit: 2, hinweis: "Bei erforderlichen Zwischenschritten (bsp. Exchange 2010 -> 2019) doppelt kalkulieren!!" },
      { id: 10, beschreibung: "Konfiguration Exchange DAG", std_pro_einheit: 1 },
      { id: 11, beschreibung: "Migration Exchange-Mailbox pro User", std_pro_einheit: 0.1 },
      { id: 12, beschreibung: "Migration Public Folder-Datenbank pro PF", std_pro_einheit: 0.5 }
    ]
  },
  {
    kategorie: "Mail-System & Online Services",
    services: [
      { id: 13, beschreibung: "Konfiguration Mail-Filtertools", std_pro_einheit: 1 },
      { id: 14, beschreibung: "Grundkonfiguration Exchange Online Admin-Portal", std_pro_einheit: 0.5 },
      { id: 15, beschreibung: "Migration zu Exchange Online mit Office 365 pro Konto", std_pro_einheit: 1 },
      { id: 16, beschreibung: "Neueinrichtung Exchange Online 365 ohne bisherigen Exchange", std_pro_einheit: 0.5 },
      { id: 17, beschreibung: "Konfiguration Azure AD-Sync", std_pro_einheit: 1 }
    ]
  },
  {
    kategorie: "Security & Monitoring",
    services: [
      { id: 18, beschreibung: "Neuinstallation/Migration SEPM", std_pro_einheit: 1 },
      { id: 19, beschreibung: "Rollout SEPM-Pakete, ggf. Konfiguration Policies", std_pro_einheit: 1 },
      { id: 20, beschreibung: "Rollout Sophos", std_pro_einheit: 0.2 },
      { id: 21, beschreibung: "Rollout/Prüfung Monitoring", std_pro_einheit: 0.2 }
    ]
  },
  {
    kategorie: "Spezielle Services",
    services: [
      { id: 22, beschreibung: "Installation/Migration Mailstore", std_pro_einheit: 1 },
      { id: 23, beschreibung: "Migration pro Fachanwendung (Zeitschätzung circa und in Kooperation mit dem Softwarehersteller)", std_pro_einheit: 4 },
      { id: 24, beschreibung: "Migration File-Server, neue Shares, ACL", std_pro_einheit: 2 },
      { id: 25, beschreibung: "Installation/Konfiguration/Lizensierung Terminal Server", std_pro_einheit: 3 }
    ]
  }
];

export default function CalculationSection({
  customers,
  calculationForm,
  setCalculationForm,
  mwst,
  setMwst,
  loading,
  onSubmit,
  isDark,
}) {
  // Dark Mode Klassen
  const bgClass = isDark ? 'bg-gray-800' : 'bg-white';
  const borderClass = isDark ? 'border-gray-700' : 'border-gray-200';
  const textClass = isDark ? 'text-gray-100' : 'text-gray-900';
  const textSecondaryClass = isDark ? 'text-gray-300' : 'text-gray-700';
  const textMutedClass = isDark ? 'text-gray-400' : 'text-gray-500';
  const inputClass = isDark 
    ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400 focus:ring-blue-500 focus:border-blue-500'
    : 'bg-white border-gray-300 text-gray-900 placeholder-gray-500 focus:ring-blue-500 focus:border-blue-500';
  const bgHoverClass = isDark ? 'hover:bg-gray-700' : 'hover:bg-gray-50';
  const tableHeaderClass = isDark ? 'bg-gray-700' : 'bg-gray-50';
  const sumBoxClass = isDark ? 'bg-gray-700 border-gray-600' : 'bg-blue-50 border-blue-200';
  const headerBgClass = isDark ? 'bg-gray-750' : 'bg-gray-100';
  const categoryBgClass = isDark ? 'bg-gray-750' : 'bg-gray-100';

  // Initialize predefined services if not already done
  React.useEffect(() => {
    if (!calculationForm.dienstleistungen || calculationForm.dienstleistungen.length === 0) {
      const allServices = PREDEFINED_SERVICES.flatMap(cat => 
        cat.services.map(service => ({
          id: service.id,
          kategorie: cat.kategorie,
          beschreibung: service.beschreibung,
          std_pro_einheit: service.std_pro_einheit,
          anzahl: 0, // Startet mit 0, User trägt ein was benötigt wird
          info: service.hinweis || '',
          stundensatz: undefined,
          rabatt: 0,
          isActive: false // Nur aktive werden in Berechnung einbezogen
        }))
      );
      
      setCalculationForm(prev => ({
        ...prev,
        dienstleistungen: allServices
      }));
    }
  }, []);

  const updateDienstleistung = (index, field, value) => {
    setCalculationForm((prev) => {
      const copy = [...prev.dienstleistungen];
      copy[index] = { ...copy[index], [field]: value };
      
      // Automatisch aktivieren wenn Anzahl > 0
      if (field === 'anzahl') {
        copy[index].isActive = Number(value) > 0;
      }
      
      return { ...prev, dienstleistungen: copy };
    });
  };

  // Rechen-Helper - nur aktive Services
  const activeServices = calculationForm.dienstleistungen?.filter(d => d.isActive && Number(d.anzahl) > 0) || [];
  
  const rowHours = (d) => (Number(d.std_pro_einheit) || 0) * (Number(d.anzahl) || 0);
  const rowRate = (d) => Number(d.stundensatz ?? calculationForm.stundensatz) || 0;
  const rowSubtotal = (d) => rowHours(d) * rowRate(d);
  const rowDiscount = (d) => rowSubtotal(d) * ((Number(d.rabatt) || 0) / 100);
  const rowTotal = (d) => rowSubtotal(d) - rowDiscount(d);

  const sumNetto = activeServices.reduce((acc, d) => acc + rowTotal(d), 0);
  const totalRabatt = activeServices.reduce((acc, d) => acc + rowDiscount(d), 0);
  const sumMwst = sumNetto * (Number(mwst) / 100);
  const sumBrutto = sumNetto + sumMwst;
  const totalStunden = activeServices.reduce((acc, d) => acc + rowHours(d), 0);

  // Heute's Datum für Kalkulation
  const today = new Date().toLocaleDateString('de-DE');

  // Group services by category
  const groupedServices = React.useMemo(() => {
    if (!calculationForm.dienstleistungen) return [];
    
    return PREDEFINED_SERVICES.map(cat => ({
      ...cat,
      services: cat.services.map(service => 
        calculationForm.dienstleistungen.find(d => d.id === service.id)
      ).filter(Boolean)
    }));
  }, [calculationForm.dienstleistungen]);

  return (
    <div className={`${bgClass} ${borderClass} rounded-lg shadow-lg border overflow-hidden`}>
      {/* Header */}
      <div className={`${headerBgClass} ${borderClass} border-b px-6 py-4`}>
        <div className="flex items-center justify-between">
          <div>
            <h2 className={`text-xl font-bold ${textClass}`}>Kalkulation - Pauly Service GmbH</h2>
            <p className={`text-sm ${textMutedClass}`}>Erstellt am: {today}</p>
          </div>
          <div className="text-right">
            <p className={`text-sm ${textMutedClass}`}>Kalk.-Nr.</p>
            <p className={`font-mono text-lg ${textClass}`}>
              {calculationForm.kunde_id ? `K${calculationForm.kunde_id}-${Date.now().toString().slice(-6)}` : '---'}
            </p>
          </div>
        </div>
      </div>

      <form onSubmit={onSubmit} className="p-6 space-y-6">
        {/* Kopfdaten */}
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          <div className="lg:col-span-2">
            <label className={`block text-sm font-semibold ${textSecondaryClass} mb-2`}>
              Kunde auswählen *
            </label>
            <select
              required
              value={calculationForm.kunde_id}
              onChange={(e) => setCalculationForm({ ...calculationForm, kunde_id: e.target.value })}
              className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 ${inputClass}`}
            >
              <option value="">Kunde wählen...</option>
              {customers.map((k) => (
                <option key={k.kunden_id} value={k.kunden_id}>
                  {k.firmenname}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className={`block text-sm font-semibold ${textSecondaryClass} mb-2`}>
              Standard-Stundensatz *
            </label>
            <div className="relative">
              <input
                type="number"
                required
                step="0.01"
                min="0"
                value={calculationForm.stundensatz}
                onChange={(e) =>
                  setCalculationForm({ ...calculationForm, stundensatz: Number(e.target.value) })
                }
                className={`w-full px-3 py-2 pr-8 border rounded-lg focus:outline-none focus:ring-2 ${inputClass}`}
                placeholder="85.00"
              />
              <span className={`absolute right-3 top-2 text-sm ${textMutedClass}`}>€</span>
            </div>
          </div>

          <div>
            <label className={`block text-sm font-semibold ${textSecondaryClass} mb-2`}>
              Mehrwertsteuer
            </label>
            <div className="relative">
              <input
                type="number"
                step="0.1"
                min="0"
                max="100"
                value={mwst}
                onChange={(e) => setMwst(Number(e.target.value))}
                className={`w-full px-3 py-2 pr-8 border rounded-lg focus:outline-none focus:ring-2 ${inputClass}`}
              />
              <span className={`absolute right-3 top-2 text-sm ${textMutedClass}`}>%</span>
            </div>
          </div>
        </div>

        {/* Übersicht Box - nur aktive Services */}
        <div className={`${sumBoxClass} rounded-lg p-4 border`}>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
            <div>
              <p className={`text-xs font-medium ${textMutedClass} uppercase tracking-wide`}>Aktive Positionen</p>
              <p className={`text-2xl font-bold ${textClass}`}>{activeServices.length}</p>
            </div>
            <div>
              <p className={`text-xs font-medium ${textMutedClass} uppercase tracking-wide`}>Gesamt-Stunden</p>
              <p className={`text-2xl font-bold ${textClass}`}>{totalStunden.toFixed(2)}</p>
            </div>
            <div>
              <p className={`text-xs font-medium ${textMutedClass} uppercase tracking-wide`}>Rabatt gesamt</p>
              <p className={`text-2xl font-bold text-orange-600`}>
                {totalRabatt.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
              </p>
            </div>
            <div>
              <p className={`text-xs font-medium ${textMutedClass} uppercase tracking-wide`}>Netto-Summe</p>
              <p className={`text-2xl font-bold text-green-600`}>
                {sumNetto.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
              </p>
            </div>
          </div>
        </div>

        {/* Service-Kategorien */}
        <div className="space-y-6">
          <h4 className={`text-lg font-semibold ${textClass}`}>Verfügbare Dienstleistungen</h4>
          
          {groupedServices.map((category, catIndex) => (
            <div key={catIndex} className={`border ${borderClass} rounded-lg overflow-hidden`}>
              {/* Kategorie Header */}
              <div className={`${categoryBgClass} px-4 py-3 border-b ${borderClass}`}>
                <h5 className={`font-semibold ${textClass}`}>{category.kategorie}</h5>
              </div>
              
              {/* Services in dieser Kategorie */}
              <div className="overflow-x-auto">
                <table className="w-full text-sm">
                  <thead className={`${tableHeaderClass} border-b ${borderClass}`}>
                    <tr>
                      <th className={`px-4 py-3 text-left font-semibold ${textSecondaryClass} min-w-[400px]`}>Dienstleistung</th>
                      <th className={`px-3 py-3 text-right font-semibold ${textSecondaryClass} w-20`}>Anzahl</th>
                      <th className={`px-3 py-3 text-right font-semibold ${textSecondaryClass} w-24`}>Std/Einheit</th>
                      <th className={`px-3 py-3 text-right font-semibold ${textSecondaryClass} w-28`}>Stundensatz</th>
                      <th className={`px-3 py-3 text-right font-semibold ${textSecondaryClass} w-20`}>Rabatt %</th>
                      <th className={`px-3 py-3 text-right font-semibold ${textSecondaryClass} w-24`}>Gesamt Std.</th>
                      <th className={`px-3 py-3 text-right font-semibold ${textSecondaryClass} w-28`}>Betrag</th>
                      <th className={`px-4 py-3 text-left font-semibold ${textSecondaryClass} min-w-[120px]`}>Notiz</th>
                    </tr>
                  </thead>
                  <tbody className={`divide-y ${borderClass}`}>
                    {category.services.map((service) => {
                      const serviceIndex = calculationForm.dienstleistungen?.findIndex(d => d.id === service.id) ?? -1;
                      if (serviceIndex === -1) return null;
                      
                      const d = calculationForm.dienstleistungen[serviceIndex];
                      const hours = rowHours(d);
                      const rate = rowRate(d);
                      const subtotal = rowSubtotal(d);
                      const discount = rowDiscount(d);
                      const total = rowTotal(d);
                      const hasDiscount = (Number(d.rabatt) || 0) > 0;
                      const isActive = d.isActive && Number(d.anzahl) > 0;
                      
                      return (
                        <tr key={service.id} className={`${bgClass} ${bgHoverClass} transition-colors ${isActive ? 'ring-2 ring-blue-200' : ''}`}>
                          <td className="px-4 py-3">
                            <div className="flex items-start">
                              <div className="flex-1">
                                <p className={`font-medium ${isActive ? 'text-blue-600' : textClass}`}>
                                  {d.beschreibung}
                                </p>
                                {d.info && d.info.includes('doppelt kalkulieren') && (
                                  <p className={`text-xs text-orange-600 mt-1 font-medium`}>
                                    ⚠️ {d.info}
                                  </p>
                                )}
                              </div>
                            </div>
                          </td>
                          <td className="px-3 py-3 text-right">
                            <input
                              type="number"
                              min="0"
                              value={d.anzahl || ''}
                              onChange={(e) => updateDienstleistung(serviceIndex, 'anzahl', Number(e.target.value) || 0)}
                              className={`w-full text-right px-2 py-2 border rounded-md focus:outline-none focus:ring-1 ${inputClass} ${isActive ? 'ring-2 ring-blue-300' : ''}`}
                              placeholder="0"
                            />
                          </td>
                          <td className={`px-3 py-3 text-right tabular-nums ${textMutedClass}`}>
                            {d.std_pro_einheit.toFixed(2)}
                          </td>
                          <td className="px-3 py-3">
                            <input
                              type="number"
                              step="0.01"
                              min="0"
                              value={d.stundensatz ?? ''}
                              onChange={(e) =>
                                updateDienstleistung(
                                  serviceIndex,
                                  'stundensatz',
                                  e.target.value === '' ? undefined : Number(e.target.value)
                                )
                              }
                              className={`w-full text-right px-2 py-2 border rounded-md focus:outline-none focus:ring-1 ${inputClass}`}
                              placeholder={String(calculationForm.stundensatz)}
                            />
                          </td>
                          <td className="px-3 py-3 text-right">
                            <input
                              type="number"
                              step="0.1"
                              min="0"
                              max="100"
                              value={d.rabatt || ''}
                              onChange={(e) =>
                                updateDienstleistung(serviceIndex, 'rabatt', e.target.value === '' ? 0 : Number(e.target.value))
                              }
                              className={`w-full text-right px-2 py-2 border rounded-md focus:outline-none focus:ring-1 ${inputClass}`}
                              placeholder="0"
                            />
                          </td>
                          <td className={`px-3 py-3 text-right tabular-nums font-medium ${isActive ? 'text-blue-600' : textClass}`}>
                            {isActive ? hours.toFixed(2) : '0.00'}
                          </td>
                          <td className="px-3 py-3 text-right">
                            {isActive ? (
                              <div className="space-y-1">
                                {hasDiscount && (
                                  <div className={`text-xs ${textMutedClass} line-through`}>
                                    {subtotal.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
                                  </div>
                                )}
                                <div className={`tabular-nums font-semibold ${hasDiscount ? 'text-green-600' : 'text-blue-600'}`}>
                                  {total.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
                                </div>
                              </div>
                            ) : (
                              <span className={`text-sm ${textMutedClass}`}>-</span>
                            )}
                          </td>
                          <td className="px-4 py-3">
                            <input
                              type="text"
                              value={d.info || ''}
                              onChange={(e) => updateDienstleistung(serviceIndex, 'info', e.target.value)}
                              className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-1 ${inputClass}`}
                              placeholder="Zusätzliche Notizen..."
                            />
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          ))}
        </div>

        {/* Summenbereich */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2">
            {/* Platz für weitere Infos */}
          </div>
          
          <div className={`${sumBoxClass} rounded-lg p-6 border space-y-4`}>
            <h5 className={`text-lg font-bold ${textClass} border-b ${borderClass} pb-2`}>
              Zusammenfassung
            </h5>
            
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <span className={`text-sm ${textSecondaryClass}`}>Aktive Positionen</span>
                <span className={`font-medium ${textClass}`}>{activeServices.length}</span>
              </div>
              
              <div className="flex items-center justify-between">
                <span className={`text-sm ${textSecondaryClass}`}>Gesamtstunden</span>
                <span className={`font-medium ${textClass}`}>{totalStunden.toFixed(2)} h</span>
              </div>
              
              {totalRabatt > 0 && (
                <div className="flex items-center justify-between text-orange-600">
                  <span className="text-sm">Rabatt gesamt</span>
                  <span className="font-medium">
                    -{totalRabatt.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
                  </span>
                </div>
              )}
              
              <div className={`flex items-center justify-between border-t ${borderClass} pt-3`}>
                <span className={`text-sm ${textSecondaryClass}`}>Summe Netto</span>
                <span className={`font-semibold ${textClass}`}>
                  {sumNetto.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
                </span>
              </div>
              
              <div className="flex items-center justify-between">
                <span className={`text-sm ${textSecondaryClass}`}>MwSt ({mwst}%)</span>
                <span className={`font-semibold ${textClass}`}>
                  {sumMwst.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
                </span>
              </div>
              
              <div className={`flex items-center justify-between border-t-2 ${borderClass} pt-3 border-double`}>
                <span className={`font-bold ${textClass}`}>Summe Brutto</span>
                <span className={`text-xl font-bold text-blue-600`}>
                  {sumBrutto.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Aktions-Buttons */}
        <div className="flex flex-col sm:flex-row gap-3 justify-end pt-6">
          <button
            type="button"
            className={`px-6 py-3 border ${borderClass} ${textSecondaryClass} rounded-lg hover:bg-gray-50 transition-colors`}
          >
            Drucken / PDF
          </button>
          <button
            type="submit"
            disabled={loading || activeServices.length === 0}
            className="px-8 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors font-semibold"
          >
            {loading ? 'Speichern...' : 'Kalkulation speichern'}
          </button>
        </div>
      </form>
    </div>
  );
}
