import React, { useState } from 'react';
import { User, Building, Network, Server, Mail, Shield, HardDrive, Settings, ChevronRight, CheckCircle, AlertCircle } from 'lucide-react';

export default function ExtendedCustomerOnboarding() {
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);

  // Kundendaten (Schritt 1)
  const [customerData, setCustomerData] = useState({
    firmenname: '',
    strasse: '',
    hausnummer: '',
    ort: '',
    plz: '',
    telefonnummer: '',
    email: '',
    ansprechpartner: {
      name: '',
      vorname: '',
      email: '',
      telefonnummer: '',
      position: ''
    }
  });

  // IT-Infrastruktur Daten (Schritt 2)
  const [infrastructureData, setInfrastructureData] = useState({
    internet: {
      zugang: '',
      feste_ip: false,
      firewall_modell: '',
      firewall_alter: '',
      vpn_erforderlich: false,
      vpn_user_anzahl: 0
    },
    users: {
      netz_user_anzahl: 0,
      mail_user_anzahl: 0
    },
    hardware: {
      server_netzteile: 'ja',
      hot_spare_hdd: 'ja',
      raid_level: '',
      usv_vorhanden: false,
      usv_modell: '',
      drucker: []
    },
    mail: {
      file_server_volumen: '',
      mail_server_volumen: '',
      mail_speicherort: '',
      pop3_connector: false,
      sonstige_mailadressen: 0,
      besondere_anforderungen: '',
      mobiler_zugriff: false,
      zertifikat_erforderlich: false
    },
    software: {
      verwendete_applikationen: [],
      server_applikationen: [],
      neue_server_apps: [],
      ansprechpartner: '',
      wartungsvertrag: false,
      migration_support: false,
      virenschutz: '',
      schnittstellen: ''
    },
    backup: {
      strategie: '',
      nas_vorhanden: false,
      externe_hdds: 0,
      dokumentation_vorhanden: false,
      admin_passwoerter_bekannt: false
    }
  });

  const steps = [
    { id: 1, title: 'Kundendaten', icon: Building, description: 'Firmendaten und Kontakt' },
    { id: 2, title: 'IT-Infrastruktur', icon: Network, description: 'Technische Dokumentation' },
    { id: 3, title: 'Bestätigung', icon: CheckCircle, description: 'Daten prüfen und speichern' }
  ];

  const handleCustomerSubmit = () => {
    if (!customerData.firmenname || !customerData.email) {
      alert('Bitte füllen Sie mindestens Firmenname und E-Mail aus');
      return;
    }
    setCurrentStep(2);
  };

  const handleInfrastructureSubmit = () => {
    setCurrentStep(3);
  };

  const handleFinalSubmit = async () => {
    setLoading(true);
    
    try {
      const customerResponse = await fetch('http://localhost:5000/api/customers', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(customerData)
      });

      if (!customerResponse.ok) throw new Error('Fehler beim Erstellen des Kunden');

      const customerResult = await customerResponse.json();
      const kundeId = customerResult.kunde.kunden_id;

      const onboardingResponse = await fetch('http://localhost:5000/api/onboarding', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          kunde_id: kundeId,
          infrastructure_data: infrastructureData
        })
      });

      if (!onboardingResponse.ok) throw new Error('Fehler beim Erstellen des Onboardings');

      alert('✅ Kunde und IT-Infrastruktur erfolgreich erfasst!');
      setCurrentStep(1);
      setCustomerData({
        firmenname: '', strasse: '', hausnummer: '', ort: '', plz: '', telefonnummer: '', email: '',
        ansprechpartner: { name: '', vorname: '', email: '', telefonnummer: '', position: '' }
      });
      
    } catch (error) {
      console.error('Error:', error);
      alert('❌ Fehler beim Speichern: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const renderStep1 = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Firmendaten</h3>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Firmenname *</label>
            <input
              type="text"
              required
              value={customerData.firmenname}
              onChange={(e) => setCustomerData({...customerData, firmenname: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Firmenname"
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">E-Mail *</label>
            <input
              type="email"
              required
              value={customerData.email}
              onChange={(e) => setCustomerData({...customerData, email: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="firma@example.com"
            />
          </div>

          {/* Additional fields for step 1 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Straße *</label>
            <input
              type="text"
              value={customerData.strasse}
              onChange={(e) => setCustomerData({...customerData, strasse: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Musterstraße"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Hausnummer *</label>
            <input
              type="text"
              value={customerData.hausnummer}
              onChange={(e) => setCustomerData({...customerData, hausnummer: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="123a"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">PLZ *</label>
            <input
              type="text"
              value={customerData.plz}
              onChange={(e) => setCustomerData({...customerData, plz: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="12345"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Ort *</label>
            <input
              type="text"
              value={customerData.ort}
              onChange={(e) => setCustomerData({...customerData, ort: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Musterstadt"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Telefon *</label>
            <input
              type="tel"
              value={customerData.telefonnummer}
              onChange={(e) => setCustomerData({...customerData, telefonnummer: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="+49 123 456789"
            />
          </div>
        </div>

        <div className="border-t pt-6 mt-6">
          <h4 className="text-md font-medium text-gray-900 mb-4">Ansprechpartner</h4>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Vorname</label>
              <input
                type="text"
                value={customerData.ansprechpartner.vorname}
                onChange={(e) => setCustomerData({
                  ...customerData, 
                  ansprechpartner: {...customerData.ansprechpartner, vorname: e.target.value}
                })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Max"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Nachname</label>
              <input
                type="text"
                value={customerData.ansprechpartner.name}
                onChange={(e) => setCustomerData({
                  ...customerData, 
                  ansprechpartner: {...customerData.ansprechpartner, name: e.target.value}
                })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Mustermann"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Position</label>
              <input
                type="text"
                value={customerData.ansprechpartner.position}
                onChange={(e) => setCustomerData({
                  ...customerData, 
                  ansprechpartner: {...customerData.ansprechpartner, position: e.target.value}
                })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="IT-Leiter"
              />
            </div>
          </div>
        </div>

        <div className="flex justify-end mt-6">
          <button
            onClick={handleCustomerSubmit}
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors"
          >
            Weiter zur IT-Infrastruktur →
          </button>
        </div>
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className="space-y-6">
      {/* Internet & Firewall section */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center mb-4">
          <Shield className="w-5 h-5 text-blue-600 mr-2" />
          <h3 className="text-lg font-medium text-gray-900">Internet & Firewall</h3>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Welcher Internetzugang ist vorhanden?</label>
            <input
              type="text"
              value={infrastructureData.internet.zugang}
              onChange={(e) => setInfrastructureData({
                ...infrastructureData,
                internet: {...infrastructureData.internet, zugang: e.target.value}
              })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="z.B. DSL 100/40 Mbit, Glasfaser..."
            />
          </div>

          {/* Additional step 2 fields */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Firewall Modell & Alter</label>
            <input
              type="text"
              value={infrastructureData.internet.firewall_modell}
              onChange={(e) => setInfrastructureData({
                ...infrastructureData,
                internet: {...infrastructureData.internet, firewall_modell: e.target.value}
              })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="z.B. Sophos XG 125, 2 Jahre alt"
            />
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="feste_ip"
              checked={infrastructureData.internet.feste_ip}
              onChange={(e) => setInfrastructureData({
                ...infrastructureData,
                internet: {...infrastructureData.internet, feste_ip: e.target.checked}
              })}
              className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
            />
            <label htmlFor="feste_ip" className="ml-2 text-sm text-gray-700">
              Feste IP-Adresse vom ISP vorhanden
            </label>
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="vpn_erforderlich"
              checked={infrastructureData.internet.vpn_erforderlich}
              onChange={(e) => setInfrastructureData({
                ...infrastructureData,
                internet: {...infrastructureData.internet, vpn_erforderlich: e.target.checked}
              })}
              className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
            />
            <label htmlFor="vpn_erforderlich" className="ml-2 text-sm text-gray-700">
              VPN-Einwahl erforderlich
            </label>
          </div>

          {infrastructureData.internet.vpn_erforderlich && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Anzahl VPN-User</label>
              <input
                type="number"
                value={infrastructureData.internet.vpn_user_anzahl}
                onChange={(e) => setInfrastructureData({
                  ...infrastructureData,
                  internet: {...infrastructureData.internet, vpn_user_anzahl: parseInt(e.target.value) || 0}
                })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                min="0"
              />
            </div>
          )}
        </div>
      </div>

      {/* Additional sections for step 2 */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center mb-4">
          <User className="w-5 h-5 text-green-600 mr-2" />
          <h3 className="text-lg font-medium text-gray-900">Benutzer</h3>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Anzahl User im Netz</label>
            <input
              type="number"
              value={infrastructureData.users.netz_user_anzahl}
              onChange={(e) => setInfrastructureData({
                ...infrastructureData,
                users: {...infrastructureData.users, netz_user_anzahl: parseInt(e.target.value) || 0}
              })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              min="0"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Anzahl User mit Mail-Konto</label>
            <input
              type="number"
              value={infrastructureData.users.mail_user_anzahl}
              onChange={(e) => setInfrastructureData({
                ...infrastructureData,
                users: {...infrastructureData.users, mail_user_anzahl: parseInt(e.target.value) || 0}
              })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              min="0"
            />
          </div>
        </div>
      </div>

      <div className="flex justify-between">
        <button
          onClick={() => setCurrentStep(1)}
          className="px-6 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 transition-colors"
        >
          ← Zurück zu Kundendaten
        </button>
        <button
          onClick={handleInfrastructureSubmit}
          className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors"
        >
          Weiter zur Bestätigung →
        </button>
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Zusammenfassung</h3>
        
        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-gray-700">Kunde:</h4>
            <p className="text-gray-600">{customerData.firmenname} - {customerData.email}</p>
          </div>
          
          <div>
            <h4 className="font-medium text-gray-700">Benutzer:</h4>
            <p className="text-gray-600">
              {infrastructureData.users.netz_user_anzahl} Netzwerk-User, 
              {infrastructureData.users.mail_user_anzahl} Mail-User
            </p>
          </div>
          
          <div>
            <h4 className="font-medium text-gray-700">Internet:</h4>
            <p className="text-gray-600">{infrastructureData.internet.zugang || 'Nicht angegeben'}</p>
          </div>
          
          <div>
            <h4 className="font-medium text-gray-700">Software:</h4>
            <p className="text-gray-600">
              {infrastructureData.software.verwendete_applikationen.length > 0 
                ? infrastructureData.software.verwendete_applikationen.slice(0, 3).join(', ') + 
                  (infrastructureData.software.verwendete_applikationen.length > 3 ? '...' : '')
                : 'Nicht angegeben'
              }
            </p>
          </div>
        </div>

        <div className="flex justify-between mt-6">
          <button
            onClick={() => setCurrentStep(2)}
            className="px-6 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 transition-colors"
          >
            ← Zurück zur IT-Infrastruktur
          </button>
          <button
            onClick={handleFinalSubmit}
            disabled={loading}
            className="px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-50 focus:outline-none focus:ring-2 focus:ring-green-500 transition-colors"
          >
            {loading ? 'Speichern...' : '✅ Kunde und IT-Infrastruktur speichern'}
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-6xl mx-auto p-6">
      {/* Progress Steps */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          {steps.map((step) => (
            <div key={step.id} className="flex items-center">
              <div className={`flex items-center justify-center w-10 h-10 rounded-full border-2 ${
                currentStep >= step.id 
                  ? 'bg-blue-600 border-blue-600 text-white' 
                  : 'bg-white border-gray-300 text-gray-400'
              }`}>
                {currentStep > step.id ? (
                  <CheckCircle className="w-5 h-5" />
                ) : (
                  <step.icon className="w-5 h-5" />
                )}
              </div>
              <div className="ml-3">
                <p className={`text-sm font-medium ${
                  currentStep >= step.id ? 'text-blue-600' : 'text-gray-400'
                }`}>
                  {step.title}
                </p>
                <p className="text-xs text-gray-500">{step.description}</p>
              </div>
              {step.id < steps.length && (
                <ChevronRight className="w-5 h-5 text-gray-400 mx-4" />
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Step Content */}
      {currentStep === 1 && renderStep1()}
      {currentStep === 2 && renderStep2()}
      {currentStep === 3 && renderStep3()}
    </div>
  );
}
